mapper.insert:
-- 删除已经移除的门店联系人 delete from dealer_contact where dealer_id = #{dealerId}
AND dealer_contact_id NOT IN (
#{item.dealerContactId}
)
-- 新增门店联系人 insert into dealer_contact(dealer_contact_id,dealer_id,contact_name,phone,email,qq) values
( #{item.dealerContactId}, #{dealerId}, #{item.contact_name}, #{item.phone}, #{item.email}, #{item.qq} )
